function move(percentage)
{var range=timeline.getWindow();var interval=range.end-range.start;timeline.setWindow({start:range.start.valueOf()-interval*percentage,end:range.end.valueOf()-interval*percentage});}
function zoom(percentage)
{var range=timeline.getWindow();var interval=range.end-range.start;timeline.setWindow({start:range.start.valueOf()-interval*percentage,end:range.end.valueOf()+interval*percentage});}
document.getElementById('zoomInTimeline').onclick=function(){zoom(-0.2);};document.getElementById('zoomOutTimeline').onclick=function(){zoom(0.2);};document.getElementById('moveLeftTimeline').onclick=function(){move(0.2);};document.getElementById('moveRightTimeline').onclick=function(){move(-0.2);};