function move(percentage)
{var range=timeline.getWindow();var interval=range.end-range.start;timeline.setWindow({start:range.start.valueOf()-interval*percentage,end:range.end.valueOf()-interval*percentage});}
function zoom(percentage)
{var range=timeline.getWindow();var interval=range.end-range.start;timeline.setWindow({start:range.start.valueOf()-interval*percentage,end:range.end.valueOf()+interval*percentage});}
document.getElementById('zoomIn').onclick=function(){zoom(-0.2);};document.getElementById('zoomOut').onclick=function(){zoom(0.2);};document.getElementById('moveLeft').onclick=function(){move(0.2);};document.getElementById('moveRight').onclick=function(){move(-0.2);};